import React, { useState, useEffect } from "react";
import Layout from "./../../components/Layout/Layout";
import AdminMenu from "./../../components/Layout/AdminMenu";
import toast from "react-hot-toast";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Modal } from "antd";


const CreateWarehouse = () => {
    const navigate = useNavigate();
    const [warehouses, setWarehouses] = useState([]);
    const [name, setName] = useState("");
    const [province, setProvince] = useState("");
    const [city, setCity] = useState("");
    const [district, setDistrict] = useState("");
    const [street, setStreet] = useState("");
    const [number, setNumber] = useState("");
    const [totalAreaVolume, setTotalArea] = useState("");
    const [visible, setVisible] = useState(false);
    const [selected, setSelected] = useState(null);
    const [updatedName, setUpdatedName] = useState("");
    const handleCreate = async (e) => {
        e.preventDefault();
        try {
            const warehouseData = new FormData();
            warehouseData.append("name", name);
            warehouseData.append("province", province);
            warehouseData.append("city", city);
            warehouseData.append("district", district);
            warehouseData.append("street", street);
            warehouseData.append("number", number);
            warehouseData.append("totalAreaVolume", totalAreaVolume);
            warehouseData.append("availableVolume", totalAreaVolume);
            const { data } = axios.post(
                "/api/v1/warehouse/create-warehouse",
                warehouseData
            );
            if (data?.success) {
                toast.error(data?.message);
                console.log(data)
            } else {
                toast.success("warehouse Created Successfully");
                getAllWarehouse();
            }
        } catch (error) {
            console.log(error);
            toast.error("something went wrong");
        }
    };

    const getAllWarehouse = async () => {
        try {
            const { data } = await axios.get("/api/v1/warehouse/get-warehouse");
            setWarehouses(data?.warehouses);
            console.log("warehouse:", data?.warehouses);
        } catch (error) {
            console.log(error);
            toast.error("Someething Went Wrong");
        }
    };

    useEffect(() => {
        getAllWarehouse();
    }, []);

    const handleDelete = async (name) => {
        try {
            const { data } = await axios.delete(
                `/api/v1/warehouse/delete-warehouse/${name}`
            );
            if (data.success) {
                toast.success(`warehouse is deleted`);

                getAllWarehouse();
            } else {
                toast.error(data.message);
            }
        } catch (error) {
            toast.error("Somtihing went wrong");
        }
    };

    return (
        <Layout title={"Dashboard - Create warehouse"}>
            <div className="container-fluid m-3 p-3 dashboard">
                <div className="row">
                    <div className="col-md-3">
                        <AdminMenu />
                    </div>
                    <div className="col-md-9">
                        <h1>Create warehouse</h1>
                        <div className="m-1 w-75">
                            <div className="mb-3">
                                <input
                                    type="text"
                                    value={name}
                                    placeholder="write a name"
                                    className="form-control"
                                    onChange={(e) => setName(e.target.value)}
                                />
                            </div>
                            <div className="mb-3">
                                <input
                                    type="text"
                                    value={province}
                                    placeholder="write a Province"
                                    className="form-control"
                                    onChange={(e) => setProvince(e.target.value)}
                                />
                            </div>

                            <div className="mb-3">
                                <input
                                    type="text"
                                    value={city}
                                    placeholder="write a City"
                                    className="form-control"
                                    onChange={(e) => setCity(e.target.value)}
                                />
                            </div>
                            <div className="mb-3">
                                <input
                                    type="text"
                                    value={district}
                                    placeholder="write a district"
                                    className="form-control"
                                    onChange={(e) => setDistrict(e.target.value)}
                                />
                            </div>
                            <div className="mb-3">
                                <input
                                    type="text"
                                    value={street}
                                    placeholder="write a street"
                                    className="form-control"
                                    onChange={(e) => setStreet(e.target.value)}
                                />
                            </div>
                            <div className="mb-3">
                                <input
                                    type="text"
                                    value={number}
                                    placeholder="write a number"
                                    className="form-control"
                                    onChange={(e) => setNumber(e.target.value)}
                                />
                            </div>
                            <div className="mb-3">
                                <input
                                    type="text"
                                    value={totalAreaVolume}
                                    placeholder="write a total area"
                                    className="form-control"
                                    onChange={(e) => setTotalArea(e.target.value)}
                                />
                            </div>
                            <div className="mb-3">
                                <button className="btn btn-primary" onClick={handleCreate}>
                                    CREATE warehouse
                                </button>
                            </div>
                        </div>
                        <div className="w-75">
                            <h2>Warehouse List</h2>
                            <ul>
                                {warehouses.map((warehouse) => (
                                    <li key={warehouse.name}>
                                        <strong>Name: </strong> {warehouse.name}
                                        <strong>Province: </strong> {warehouse.province}
                                        <strong>City: </strong> {warehouse.city}
                                        <strong>District: </strong> {warehouse.district}
                                        <strong>Street: </strong> {warehouse.street}
                                        <strong>Number: </strong> {warehouse.number}
                                        <strong>Total Area/Volume: </strong> {warehouse.totalAreaVolume}
                                        {/* Add more properties as needed */}
                                        <button className="btn btn-primary ms-2" onClick={() => handleDelete(warehouse.name)}>Delete</button>
                                    </li>
                                ))}
                            </ul>
                        </div>
                        <table className="table">
                            <thead>
                                <tr>
                                    <th scope="col">Warehouse List</th>
                                </tr>
                            </thead>
                            <tbody>
                                {warehouses?.map((c) => (
                                    <>
                                        <tr>
                                            <td key={c.name}>
                                                {c.name}
                                                {c.name}
                                                {c.name}
                                                {c.name}
                                                {c.name}
                                                {c.name}
                                                {c.name}
                                            </td>
                                            <td>
                                                <button
                                                    className="btn btn-primary ms-2"
                                                    onClick={() => {
                                                        setVisible(true);
                                                        setUpdatedName(c.name);
                                                        setSelected(c);
                                                    }}
                                                >
                                                    Edit
                                                </button>
                                                <button
                                                    className="btn btn-danger ms-2"
                                                    onClick={() => {
                                                        handleDelete(c._id);
                                                    }}
                                                >
                                                    Delete
                                                </button>
                                            </td>
                                        </tr>
                                    </>
                                ))}
                            </tbody>
                        </table>
                    </div>

                </div>
            </div>
        </Layout>
    );
};

export default CreateWarehouse;
